         */
        static associate(models) {
            // define association here
        }
    }
    History.init(
        {
            patientId: DataTypes.INTEGER,
            doctorId: DataTypes.INTEGER,
            description: DataTypes.TEXT,
            files: DataTypes.TEXT,
        },
        {
            sequelize,
            modelName: 'History',
        },
    );
    return History;
};

